select song_name as nome_musica, (case when song_name like 'Dance With%' then replace('Dance With Her Own', 'Her Own', 'Trybe') when song_name like "Let's Be%" then replace("Let's Be Silly", 'Silly', 'Nice') when song_name like 'Magic%' then replace('Magic Circus', 'Circus', 'Pull Request') when song_name like 'Troubles Of My%' then replace('Troubles Of My Inner Fire', 'Inner Fire', 'Project') when song_name like 'Without My%' then replace('Without My Streets', 'Streets', 'Code Review') end) as novo_nome from SpotifyClone.song where song_name = (case when song_name like 'Dance With%' then 'Dance With Her Own' when song_name like "Let's Be%" then "Let's Be Silly" when song_name like 'Magic%' then 'Magic Circus' when song_name like 'Troubles Of My%' then 'Troubles Of My Inner Fire' when song_name like 'Without My%' then 'Without My Streets' end) order by song_name;